mapper.select:
SELECT e.`name`,v.title,v.id,ve.number FROM t_electionman e JOIN t_voting_electionman ve ON e.id=ve.eId JOIN t_voting v ON v.id=ve.vId
WHERE v.id=#{vid}
WHERE v.`status`=1

SELECT e.`name`,e.id as uid,e.img,ve.number,ve.vId FROM t_electionman e JOIN t_voting_electionman ve ON e.id=ve.eId JOIN t_voting v ON v.id=ve.vId WHERE v.`status`=1
and e.deptId=#{deptId}
and e.id=#{name}
and e.name like concat(concat(#{name}),'%')
order by ve.number desc
